import Post from "../Post/Post";
import "./Main.scss";

const stories = [
  <Story img={avatar} username="username" seen={true} />,
  <Story img={avatar} username="username" seen={true} />,
  <Story img={avatar} username="username" seen={true} />,
  <Story img={avatar} username="username" seen={true} />,
  <Story img={avatar} username="username" seen={true} />,
];

function Main() {
  const scrollRef = useRef();
  const scroll = (scrollOffset) => {
    scrollRef.current.scrollLeft += scrollOffset;
