import { getAuth, onAuthStateChanged } from "firebase/auth";
import Login from "../Components/Login/Login";
import Main from "../Components/Main/Main";

function Root() {
  const auth = getAuth();
  const user = auth.currentUser;
  if (user) {
    const uid = user.uid;
    return <Main />;
  } else {
    return <Login />;
  }
}

export default Root;
