import { doc, getDoc } from "firebase/firestore";

export const getPostWithId = async (db, id) => {
  const docRef = doc(db, "posts", "rxL2KcNEXS4glpZDvOOb");
  const docSnap = await getDoc(docRef);
  const doc = docSnap.data();
  console.log("a", doc);
  return doc;
};
