import { useRef } from "react";
import avatar from "../../assets/avatar.jpg";
import Post from "../Post/Post";
import "./Main.scss";

function Main() {
  const scrollRef = useRef();
  const scroll = (scrollOffset) => {
    ref.current.scrollLeft += scrollOffset;
  };

  return (
    <div className="main">
      <div className="feed">
        <div className="stories">
          <button className="left_button" onClick={() => scroll(-20)}></button>
          <button className="right_button" onClick={() => scroll(20)}></button>
          <div className="story-list" ref={scrollRef}>
            <Story img={avatar} username={"vefa.ozlu"} seen={true} />
            <Story img={avatar} username={"vefa.ozlu"} seen={false} />
            <Story img={avatar} username={"vefa.ozlu"} seen={true} />
            <Story img={avatar} username={"vefa.ozlu"} seen={false} />
            <Story img={avatar} username={"vefa.ozlu"} seen={false} />
            <Story img={avatar} username={"vefa.ozlu"} seen={false} />
            <Story img={avatar} username={"vefa.ozlu"} seen={true} />
            <Story img={avatar} username={"vefa.ozlu"} seen={false} />
            <Story img={avatar} username={"vefa.ozlu"} seen={true} />
          </div>
        </div>
        <div className="posts">
          <Post />
          <Post />
          <Post />
          <Post />
          <Post />
          <Post />
          <Post />
          <Post />
          <Post />
          <Post />
          <Post />
          <Post />
        </div>
      </div>
      <div className="suggestions"></div>
    </div>
  );
}

export default Main;

function Story({ img, username, seen = false }) {
  return (
    <div className="Story">
      <div className={seen ? "seen_image" : "unseen_image"}>
        <img src={img} />
      </div>
      <span className={seen ? "seen_text" : "unseen_text"}>{username}</span>
    </div>
  );
}
