import "./Post.scss";

function Post() {
  return (
    <div className="post">
      <div className="top">
        <div className="avatar-container">
          <img className="avatar" alt="profile-picture"></img>
        </div>
        <div className="username">vefa.ozlu</div>
        <MoreIcon />
      </div>
      <div className="content-container">
        <img className="content" alt="post"></img>
      </div>
      <div className="buttons-container">
        <LikeIcon />
        <CommentIcon />
        <ShareIcon />
        <span></span>
        <SaveIcon />
      </div>
      <div className="likes-container">
        <div className="liked-by-avatar">
            <img className="avatar" alt="profile-picture"></img>
        </div>
        <p>Liked by vefa.ozlu and 432 others</p>
      </div>
    </div>
  );
}

export default Post;
