import { doc, getDoc } from "firebase/firestore/lite";

export const getPost = async ({db, id}) => {
    const docRef = doc(db, "posts", id);
    const docSnap = await getDoc(docRef);
    console.log(docSnap.data());
    return docSnap.data();
}
